import React, { useState, useEffect } from 'react'
import './banner.css'
import bgImg from '../images/bg-no-hard-feelings.jpeg'
import Moviecontent from '../components/Moviecontent';
import Moviedate from '../components/Moviedate';
import PlayBtn from '../components/PlayBtn';
import Movieswiper from '../components/Movieswiper';

function Banner() {
  const [movies, setMovies] = useState([]);

  const fetchData = () => {
    fetch('movieData.json',
    {
      "_id": 8,
      "titleImg": "../assets/movies/no-hard-feelings-title.png",
      "bgImg": "../assets/movies/bg-no-hard-feelings.jpeg",
      "previewImg": "../assets/movies/no-hard-feelings.jpeg",
      "video": "https://www.youtube.com/embed/7psP7xBEa28",
      "title": "No Hard Feelings",
      "year": "2023",
      "date": "06th October",
      "ageLimit": "14+",
      "length": "1h 43min",
      "category": "Romance",
      "type": "coming",
      "description": "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi commodo felis tempor augue tristique, eget dignissim sapien ornare. Nullam vel hendrerit metus, a interdum risus. Etiam commodo lorem arcu, a tempus risus tempus sed. Proin ornare nec massa eget rhoncus. In rutrum aliquet dui, et placerat est molestie id.",
      "active": true
    },  {
      "_id": 9,
      "titleImg": "../assets/movies/peter-pan-and-wendy-title.png",
      "bgImg": "../assets/movies/bg-peter-pan-and-wendy.jpeg",
      "previewImg": "../assets/movies/peter-pan-and-wendy.jpg",
      "video": "https://www.youtube.com/embed/9Ji5U2sTlLU",
      "title": "Peter Pan & Wendy",
      "year": "2023",
      "date": "14th October",
      "ageLimit": "12+",
      "length": "1h 49min",
      "category": "Adventure",
      "type": "coming",
      "description": "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Morbi commodo felis tempor augue tristique, eget dignissim sapien ornare. Nullam vel hendrerit metus, a interdum risus. Etiam commodo lorem arcu, a tempus risus tempus sed. Proin ornare nec massa eget rhoncus. In rutrum aliquet dui, et placerat est molestie id.",
      "active": false
    },
    
    )
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(e => console.log(e.message));
  };

  useEffect(() => {
    fetchData()
  }, [])

  const handleSlideChange = id=>{
     const newMovies = movies.map(movie=>{
      movie.active=false;
      if(movie._id===id){
        movie.active=true;
      }
      return movie;
     })
     setMovies(newMovies);
  }

  return (
   <>
      <div className='banner'>
        {
          movies && movies.length>0 && movies.map(movie=>(
            <div className="movie">
            <img src={movie.bgImg} alt="Background Image" className={`bgImg ${movie.active ? 'active' : undefined}`} />
            <div className="container-fluid">
              <div className="row">
                <div className="col-log-6 col-md-12">
                  <Moviecontent movie={movie} />
  
                </div>
                <div className="col-log-6 col-md-12 ">
                  <Moviedate   movie={movie}/>
                  <PlayBtn  movie={movie}/>
                </div>
              </div>
            </div>
          </div>
          ))
        }
       
      
        {movies && movies.length > 0 && <Movieswiper slides={movies} slideChange={handleSlideChange} />}
      </div>
      
      
      </>
   
  )
}

export default Banner
