import React,{useState,useEffect}  from 'react'
import './banner.css'
import bgImg from '../images/bg-no-hard-feelings.jpeg'
import Moviecontent from '../components/Moviecontent';

function Banner() {
    const [movies,setMovies] = useState([]);

    const fetchData = () =>{
        fetch('http://localhost:3000/data/movieData.json')
        .then(res=>res.json())
        .then(data => setMovies(data))
        .catch(e=>console.log(e.message));
    };

    useEffect(()=>{
       fetchData()
    },[])

  return (
    <div className='banner active'>
      <div className="movie">
        <img src={bgImg} alt="Background Image" className="bgImg active" />
        <div className="container-fluid">
            <div className="row">
                <div className="col-log-6 col-md-12">
                   <Moviecontent/>
                </div>
                <div className="col-log-6 col-md-12">
                    <div className="date active">
                        <h2>On 15 August</h2>
                    </div>
                    <div className="trailer d-flex align-items-center justify-content-center active">
                        <a href="#" className="playBtn"><ion-icon name="play-outline"></ion-icon></a>
                        <p>Watch Trailer</p>
                    </div>
                </div>
            </div>
        </div>
      </div>
    </div>
  )
}

export default Banner
